ckground-color: #888;}.navbar-default .navbar-collapse{border-color: #e7e7e7;}.close{float: right; font-size: 21px; font-weight: 700; line-height: 1; color: #000; text-shadow: 0 1px 0 #fff; filter: alpha(opacity=20); opacity: .2;}button.close{-webkit-appearance: none; padding: 0; background: 0 0; border: 0;}.modal{position: fixed; top: 0; right: 0; bottom: 0; left: 0; z-index: 1050; display: none; overflow: hidden; -webkit-overflow-scrolling: touch; outline: 0;}.modal.fade .modal-dialog{-webkit-transform: translate3d(0,-25%,0); -o-transform: translate3d(0,-25%,0); transform: translate3d(0,-25%,0);}.modal-dialog{position: relative; width: auto; margin: 10px;}.modal-content{position: relative; background-color: #fff; -webkit-background-clip: padding-box; background-clip: padding-box; border: 1px solid #999; border: 1px solid rgba(0,0,0,.2); border-radius: 6px; outline: 0; -webkit-box-shadow: 0 3px 9px rgba(0,0,0,.5); box-shadow: 0 3px 9px rgba(0,0,0,.5);}.modal-header{min-height: 16.43px; padding: 15px; border-bottom: 1px solid #e5e5e5;}.modal-header .close{margin-top: -2px;}.modal-title{margin: 0; line-height: 1.42857143;}.modal-body{position: relative; padding: 15px;}.modal-footer{padding: 15px; text-align: right; border-top: 1px solid #e5e5e5;}@media (min-width:768px){.modal-dialog{width: 600px; margin: 30px auto;}.modal-content{-webkit-box-shadow: 0 5px 15px rgba(0,0,0,.5); box-shadow: 0 5px 15px rgba(0,0,0,.5);}}.container:before,.container:after,.row:before,.row:after,.nav:before,.nav:after,.navbar:before,.navbar:after,.navbar-header:before,.navbar-header:after,.navbar-collapse:before,.navbar-collapse:after,.modal-footer:before,.modal-footer:after{display: table; content: " ";}.container:after,.row:after,.nav:after,.navbar:after,.navbar-header:after,.navbar-collapse:after,.modal-footer:after{clear: both;}@-ms-viewport{width: device-width;}body,html{width: 100%; height: 100%;}body,h1,h3,h4{font-family: Lato,"Helvetica Neue",Helvetica,Arial,sans-serif; font-weight